mapper.select:
SELECT NO, ID, PW, NAME, PHONE, EMAIL, ADDRESS, REGDATE, STATE
  FROM MEMBER
 WHERE ID = #{id}
   AND PW = #{pw}

SELECT ID, REGDATE
  FROM MEMBER
 WHERE NAME = #{param1}
   AND PHONE = #{param2}

mapper.update:
UPDATE MEMBER
   SET PW = #{pw}
 WHERE EMAIL = #{email}

UPDATE MEMBER
  SET STATE = #{state}
WHERE NO = #{no}
